import { Avatar } from "@mui/material";
import React, { useEffect, useState } from "react";
import { data } from "../datautility/tweetdata";
import "./tweets.css";
import Edit from "@mui/icons-material/Edit";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';




const Tweets = () => {




    var sorted_data = []

    const [tweets, settweets] = useState()

    useEffect(() => {

        for (let x of data.data) {
            if (x.text) {
                for (let y of data.includes.users) {


                    if (x.author_id == y.id) {
                        var localobject = {}
                        localobject['username'] = y.username
                        localobject['name'] = y.name
                        localobject['location'] = y.location
                        localobject['text'] = x.text
                        localobject['userid'] = x.author_id
                        localobject['tweetid'] = x.id
                    }
                    // sorted_data.push(localobject)


                }
                sorted_data.push(localobject)
            }
        }
        settweets(sorted_data)




    }, [])





    return (
        <div className="tweet-body" >
            {
                tweets?.map(tweet => {
                    return (

                        <div className="tweet" style={{ display: "flex", paddingBottom: "30px" }}>
                            <div className="tweet-left">
                                <Avatar alt={tweet.name} src="/broken-image.jpg" ></Avatar>

                            </div>
                            <div className="tweet-tweet-body">
                                <div className="tweet-tweet-username" style={{ display: "flex" }}>
                                    <div>
                                        {tweet.username}
                                    </div>
                                    <div style={{ width: "100%" }}>
                                        <div style={{
                                            width: "fit-content",
                                            marginRight: " 0px",
                                            marginLeft: "auto"
                                        }} >

                                            <Edit style={{ color: "grey", paddingRight: "6px" }}></Edit>
                                            <DeleteForeverIcon style={{ paddingRight: "3px" }}></DeleteForeverIcon>
                                        </div>
                                    </div>


                                </div>
                                <div className="tweet-tweet-tweet">
                                    {tweet.text}
                                </div>
                            </div>
                        </div>
                    )
                })
            }

        </div>
    )
}

export default Tweets
